<script>
export default {
  name: "SideBar",
  setup(props) {
    const { countries, getSelectedContry, setSelectedCountry } = useCountry();
    return {
      countries,
      getSelectedContry,
      setSelectedCountry,
    };
  },
  computed: {
    async selectedContry() {
      return await this.getSelectedContry();
    },
  },
  methods: {
    isActive(name) {
      return this.$route.path.toString().split("/").includes(name);
    },
  },
};
